const english=require('./en');// import 'en.js'
const spanish=require('./es')
const arabic=require('./ar')


function greet(language){
    if(language==="en"){
        english.helloGreet();
        return
    }
    if(language==="es"){
        spanish();
        return
    }
    if(language==="ar"){
        arabic();
        return
    }
    console.log("please provide language");
}

module.exports=greet;